import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      theme: ThemeData(brightness: Brightness.light, useMaterial3: true),
      home: Scaffold(
        appBar: AppBar(
          title: Text('Carrosel de imagens - Ithalo Sousa'),
        ),
        body: Center(
          child: ImageSlider(),
        ),
      ),
    );
  }
}

class ImageSlider extends StatefulWidget {
  @override
  _ImageSliderState createState() => _ImageSliderState();
}

class _ImageSliderState extends State<ImageSlider> {
  int _currentIndex = 0;
  List<String> _imageUrls = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
  ];

  @override
  Widget build(BuildContext context) {
    return Column(
      mainAxisAlignment: MainAxisAlignment.center,
      children: [
        // Aqui você pode exibir a imagem atual
        Image.network(
          _imageUrls[_currentIndex],
          height: 200, // Ajuste a altura conforme necessário
          width: 300, // Ajuste a largura conforme necessário
        ),
        SizedBox(height: 20), // Adiciona um espaço entre a imagem e os botões
        Row(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            IconButton(
              icon: Icon(Icons.arrow_back),
              onPressed: () {
                // Lógica para mostrar a imagem anterior
                setState(() {
                  _currentIndex = (_currentIndex - 1) % _imageUrls.length;
                  if (_currentIndex < 0) {
                    _currentIndex = _imageUrls.length - 1;
                  }
                });
              },
            ),
            SizedBox(width: 20), // Adiciona um espaço entre os botões
            IconButton(
              icon: Icon(Icons.arrow_forward),
              onPressed: () {
                // Lógica para mostrar a próxima imagem
                setState(() {
                  _currentIndex = (_currentIndex + 1) % _imageUrls.length;
                });
              },
            ),
          ],
        ),
      ],
    );
  }
}
